		db.execSQL(sqlInsertCardEnc + " (457,1517)");
		db.execSQL(sqlInsertEnc + " (1517,43,'Staring out across the sea, you see a dark ship that gleams like obsidian pass between two islands, driven by oars. After rubbing your eyes, the ship is gone. Lose 1 Sanity.')");
		db.execSQL(sqlInsertCardEnc + " (457,1518)");
