		db.execSQL(sqlInsertCardEnc + " (468,1550)");
		db.execSQL(sqlInsertEnc + " (1550,43,'Sitting on the edge of a sheer sea cliff, Basil Elton clutches a mostly empty whiskey bottle and sings melancholy songs. Worried, you stay to make sure nothing happens to him. You are <i>delayed</i>.')");
		db.execSQL(sqlInsertCardEnc + " (468,1551)");
		db.execSQL(sqlInsertEnc + " (1551,47,'A brawl breaks out between a couple of the regulars, and Jonas Rigg has to fire his .45 in the air to break it up. Pass a <b>Will (-1) check</b> or move to the street with most of the startled crowd.')");
