		db.execSQL(sqlInsertCardEnc + " (437,1456)");
		db.execSQL(sqlInsertEnc + " (1456,39,'Passing a thin, pale artist who has a nasty twitch, you glance at one of his paintings and your horrified gaze is immediately captured. Pass a <b>Will (-1) check</b> or lose 1 Sanity and become <i>delayed</i> as you faint upon seeing the nightmarish creature he has depicted.')");
		db.execSQL(sqlInsertCardEnc + " (437,1457)");
		db.execSQL(sqlInsertEnc + " (1457,41,'Underneath some papers in the archive, you see a glint of light. Pass a <b>Luck (-2) check</b> to gain 1 Unique Item.')");
		db.execSQL(sqlInsertCardEnc + " (437,1458)");
		db.execSQL(sqlInsertEnc + " (1458,42,'\"Wow! That would look great next to the Babylonian scroll cylinders!\" Neil offers to buy a Unique Item from you for $3 more than its list price.')");
