		db.execSQL(sqlInsertCardEnc + " (443,1476)");
		db.execSQL(sqlInsertEnc + " (1476,42,'Neil offers to sell you a book. You may pay $3 to take the first <i><b>Tome</b></i> from the Unique Item deck.')");
