		db.execSQL(sqlInsertCardEnc + " (98,329)");
		db.execSQL(sqlInsertEnc + " (329,18,'As you leave the lodge, a green glow surrounds the door frame. Move to the street. All monsters in the sky immediately go there as well.')");
