		db.execSQL(sqlInsertEnc + " (25083,504,'As you stare up at the unfamiliar sky, you hum a song that your mother used to sing to you, and it helps to hold the madness at bay. No encounter.')");
		db.execSQL(sqlInsertCard + " (5529)");
