		db.execSQL(sqlInsertEnc + " (25084,500,'In each facet of the gem is a monstrosity waiting to be born. Make a <b>Luck (+0) check</b>. If you fail, a monster appears!')");
		db.execSQL(sqlInsertCardEnc + " (5529,25085)");
		db.execSQL(sqlInsertEnc + " (25085,508,'Great Cthulhu spares the faithful. If you wish, you may attempt a <b>Lore (-3) check</b>. If you fail, you are <b>devoured</b>. If you pass, take this card. Discard this card the next time you would be <b>devoured</b> to instead be reduced to 1 Sanity and 1 Stamina.')");
		db.execSQL(sqlInsertCardEnc + " (5529,25086)");
		db.execSQL(sqlInsertEnc + " (25086,506,'A cat brushes against your leg and meows. Regain 1 Sanity.')");
