		db.execSQL(sqlInsertCardColor + " (5530,1)");
		db.execSQL(sqlInsertCardEnc + " (5530,25087)");
		db.execSQL(sqlInsertEnc + " (25087,500,'You pick up a chestnut, and it begins to writhe in your hands. Pass a <b>Speed (-1) check</b> to fling it away. If you fail, the grub inside bursts forth, grows to impossible size, and attacks you. A monster appears!')");
		db.execSQL(sqlInsertCardEnc + " (5530,25088)");
		db.execSQL(sqlInsertEnc + " (25088,504,'The mi-go question you about the Dynasty of America, ruled by \"Emperor Castaigne.\" You must lose 2 Sanity or be <i>delayed</i>.')");
		db.execSQL(sqlInsertCardEnc + " (5530,25089)");
		db.execSQL(sqlInsertEnc + " (25089,506,'You see a young man weeping over a marble statue of a beautiful young woman. The curses he cries out against the gods chill your bones. Lose 1 Sanity.')");
		db.execSQL(sqlInsertCard + " (5531)");
		db.execSQL(sqlInsertCardExp + " (5531,4)");
		db.execSQL(sqlInsertCardColor + " (5531,1)");
		db.execSQL(sqlInsertCardEnc + " (5531,25090)");
		db.execSQL(sqlInsertEnc + " (25090,500,'You hear the call of a hunting creature in the sky above. Pass a <b>Sneak (-2) check</b> or you become its prey and lose 1 Stamina.')");
		db.execSQL(sqlInsertCardEnc + " (5531,25091)");
		db.execSQL(sqlInsertEnc + " (25091,503,'The bells ring ten, eleven, then midnight. But what does time mean, here? If you Sanity is 3 or less, you are <i>lost in time and space</i>.')");
		db.execSQL(sqlInsertCardEnc + " (5531,25092)");
		db.execSQL(sqlInsertEnc + " (25092,506,'Make a <b>Will (-2) check</b> to whisper to a sleeper in his dreams. If you pass, one investigator in Arkham of your choice gains Clue tokens equal to the number of successes you rolled.')");
		db.execSQL(sqlInsertCard + " (5532)");
