		db.execSQL(sqlInsertCardColor + " (5532,1)");
		db.execSQL(sqlInsertCardEnc + " (5532,25093)");
		db.execSQL(sqlInsertEnc + " (25093,500,'\"I am demoiselle Jeanne d''Ys,\" she says, her beautiful eyes and charming accent piercing your heart. If you stay with her, gain 1 Stamina and 1 Sanity, but you are <i>delayed</i>. If you leave her, pass a <b>Luck (-1) check</b> or lose 1 Stamina and 1 Sanity.')");
		db.execSQL(sqlInsertCardEnc + " (5532,25094)");
		db.execSQL(sqlInsertEnc + " (25094,508,'You plunge into the icy waters. Even drowning is better than seeing that terrible form again. If your Stamina is 3 or less, you are <i>lost in time and space</i>.')");
		db.execSQL(sqlInsertCardEnc + " (5532,25095)");
		db.execSQL(sqlInsertEnc + " (25095,503,'Alone and far from home you''ve struggled. Pass a <b>Will (-2) check</b> or lose 2 Sanity.')");
		db.execSQL(sqlInsertCard + " (5533)");
