		db.execSQL(sqlInsertCardColor + " (5533,1)");
		db.execSQL(sqlInsertCardEnc + " (5533,25096)");
		db.execSQL(sqlInsertEnc + " (25096,500,'Ho there, what''s this? Pass a <b>Luck (-1) check</b> to gain a Common Item.')");
		db.execSQL(sqlInsertCardEnc + " (5533,25097)");
		db.execSQL(sqlInsertEnc + " (25097,511,'Ascending through the great valley, mountainous effigies portend even less knowable encounters ahead. Lose 1 Sanity, then immediately have another encounter here.')");
		db.execSQL(sqlInsertCardEnc + " (5533,25098)");
		db.execSQL(sqlInsertEnc + " (25098,506,'\"And whosoever perseveres in delving its secrets,\" reads the faded scripture, \"will learn untold mysteries.\" You are <i>delayed</i>, but take a Clue token.')");
		db.execSQL(sqlInsertCard + " (5534)");
