		db.execSQL(sqlInsertEnc + " (25117,500,'From living blackness, a monster appears!')");
		db.execSQL(sqlInsertCardEnc + " (5540,25118)");
		db.execSQL(sqlInsertEnc + " (25118,511,'The great mountain chain shakes, and every corner of your reality with it. Roll one die for each open gate. For every failure, discard either a Clue token or an item.')");
		db.execSQL(sqlInsertCardEnc + " (5540,25119)");
