		db.execSQL(sqlInsertCardEnc + " (5541,25120)");
		db.execSQL(sqlInsertEnc + " (25120,500,'You are unsure if you are back in Arkham or not. Make a <b>Will (-2) check</b>. If you succeed, return to Arkham. If not, lose 2 Sanity as the landscape twists and warps in a nightmarish way.')");
		db.execSQL(sqlInsertCardEnc + " (5541,25121)");
		db.execSQL(sqlInsertEnc + " (25121,503,'The conical creatures wish you to return an item to your own world. They have no need for it. Gain $3.')");
		db.execSQL(sqlInsertCardEnc + " (5541,25122)");
		db.execSQL(sqlInsertEnc + " (25122,506,'Looking over the lake of Yath and taking in the fragrent air, you find yourself comforted. Gain 1 Sanity.')");
