		db.execSQL(sqlInsertEnc + " (25123,500,'You find a small gem that you plan on selling when you get the chance. Gain $3.')");
		db.execSQL(sqlInsertCardEnc + " (5542,25124)");
		db.execSQL(sqlInsertEnc + " (25124,508,'A massive wave crashes over you and washes one of your items out to sea. Choose 1 of your Common or Unique Items and discard it. If you have none, nothing happens.')");
		db.execSQL(sqlInsertCardEnc + " (5542,25125)");
		db.execSQL(sqlInsertEnc + " (25125,503,'One of the strange creatures wants to take one of your items. Pass a <b>Fight (-2) check</b> to hold onto it. if you do not, choose 1 Common or Unique Item and discard it.')");
		db.execSQL(sqlInsertCard + " (5543)");
