		db.execSQL(sqlInsertCardColor + " (5554,1)");
		db.execSQL(sqlInsertCardEnc + " (5554,25155)");
		db.execSQL(sqlInsertEnc + " (25155,500,'A strange serpent-face person in a hooded robe speaks at you in an ancient tongue. Make a <b>Lore (-2) check</b> to decipher his words and gain 1 Clue token. If you fail, he bites you, disgusted, and leaves. Lose 1 Stamina.')");
		db.execSQL(sqlInsertCardEnc + " (5554,25156)");
		db.execSQL(sqlInsertEnc + " (25156,503,'The thing that enters your mind is particularly intersted in your Spells.  For each Spell you have, gain 1 Clue token but lose 1 Sanity.')");
