		db.execSQL(sqlInsertCardEnc + " (6026,30076)");
		db.execSQL(sqlInsertEnc + " (30076,509,'You find yourself in a beautiful garden. Wandering through its lovely paths, you realize that this is the lost Hanging Garden of Babylon. Gain 2 Sanity for witnessing one of the lost wonders of the world.')");
		db.execSQL(sqlInsertCardEnc + " (6026,30077)");
