		db.execSQL(sqlInsertEnc + " (30078,500,'The shadows of men''s thoughts lengthen in the afternoon. Pass a <b>Lore (-2) check</b> to gain Clue tokens equal to the number of successes you roll.')");
		db.execSQL(sqlInsertCardEnc + " (6027,30079)");
		db.execSQL(sqlInsertEnc + " (30079,507,'The cold chills your soul and steals your memories instead of your warmth. Pass a <b>Fight (-1) check</b> or discard 2 Clue tokens.')");
		db.execSQL(sqlInsertCardEnc + " (6027,30080)");
