		db.execSQL(sqlInsertEnc + " (30081,500,'\"Is that you, Camilla?\" a voice calls to you in the darkness. If you flee, lose 1 Stamina. If you answer, pass a <b>Will (-2) check</b> or lose 2 Sanity.')");
		db.execSQL(sqlInsertCardEnc + " (6028,30082)");
		db.execSQL(sqlInsertEnc + " (30082,506,'You drift through tranquil fields that others have created as they sleep. Pass a <b>Sneak (-1) check</b> to gain a Clue token.')");
		db.execSQL(sqlInsertCardEnc + " (6028,30083)");
		db.execSQL(sqlInsertEnc + " (30083,505,'Pass a <b>Will (-3) check</b> to be allowed to leave the library, or you are <i>delayed</i>. You gain a +1 bonus to the check for each Spell you have.')");
		db.execSQL(sqlInsertCard + " (6029)");
