		db.execSQL(sqlInsertCardEnc + " (6029,30085)");
		db.execSQL(sqlInsertEnc + " (30085,507,'There is something buried in the ice. Pass a <b>Luck (-2) check</b> to pry it free without breaking it. Draw 1 Unique Item.')");
		db.execSQL(sqlInsertCardEnc + " (6029,30086)");
		db.execSQL(sqlInsertEnc + " (30086,506,'Hearing an unwholesome flapping from above, you press yourself into the shadows. A nightgaunt flies overhead, not noticing you, and drops a sheet of parchment. Draw 1 Spell.')");
		db.execSQL(sqlInsertCard + " (6030)");
