		db.execSQL(sqlInsertCardEnc + " (6030,30087)");
		db.execSQL(sqlInsertEnc + " (30087,500,'In some alternate history, you see a United States plunged into dictatorship. Suicide chambers are erected in city parks. This could be the shape of things to come. Gain 2 Clue tokens and lose 1 Sanity.')");
		db.execSQL(sqlInsertCardEnc + " (6030,30088)");
		db.execSQL(sqlInsertEnc + " (30088,503,'You find a map of this strange place. The streets are laid out in a horrible pattern! Pass a <b>Will (+1) check</b> or lose 1 Sanity.')");
		db.execSQL(sqlInsertCardEnc + " (6030,30089)");
		db.execSQL(sqlInsertEnc + " (30089,505,'You may discard any number of <i><b>Tomes</b></i> in exchange for drawing an equal number of cards from the Unique Item deck.')");
		db.execSQL(sqlInsertCard + " (6031)");
