		db.execSQL(sqlInsertCardEnc + " (6031,30090)");
		db.execSQL(sqlInsertEnc + " (30090,500,'Black stars shine upon you, and reality is reversed. If you were <i>Cursed</i>, you are now <i>Blessed</i>. If you were <i>Blessed</i>, you are now <i>Cursed</i>.')");
		db.execSQL(sqlInsertCardEnc + " (6031,30091)");
		db.execSQL(sqlInsertEnc + " (30091,503,'All cities are but a reflection of Carcosa. Make a <b>Lore (-2) check</b> to remember the street''s patterns from \"The King in Yellow.\" If you pass, you immediately return to Arkham. If you fail, you are <i>delayed</i>.')");
		db.execSQL(sqlInsertCardEnc + " (6031,30092)");
		db.execSQL(sqlInsertEnc + " (30092,506,'Others'' nightmares are not meant to be witnessed, even indirectly. Lose either 2 Sanity or 1 Stamina.')");
