		db.execSQL(sqlInsertCardEnc + " (6032,30094)");
		db.execSQL(sqlInsertEnc + " (30094,507,'Someone has taught the locals to read ancient tongues. If you appease them by discarding a <i><b>Tome</b></i>, they offer you food and shelter: Gain 2 Stamina. If you do not, you lose 1 Stamina.')");
		db.execSQL(sqlInsertCardEnc + " (6032,30095)");
		db.execSQL(sqlInsertEnc + " (30095,503,'In this alien place, mundane objects serve to remind you that Earth still exists, somewhere, somewhen. Pass a <b>Will (-3) check</b> to gain 2 Sanity. You gain a +1 bonus to the check for each Common Item you have.')");
