		db.execSQL(sqlInsertCardEnc + " (6033,30098)");
		db.execSQL(sqlInsertEnc + " (30098,506,'Sensing that fortune favors you, the Sages among Zoog-kind in the Enchanted Wood turn their attention to your troubles. If you are <i>Blessed</i>, gain the <b>Young Zoog</b> Ally card, or take 5 Clue tokens if it is unavailable.')");
		db.execSQL(sqlInsertCard + " (6034)");
