		db.execSQL(sqlInsertEnc + " (30099,500,'You find a tiny corner of placidity, and remain there for a moment. Gain 1 Stamina and 1 Sanity.')");
		db.execSQL(sqlInsertCardEnc + " (6034,30100)");
		db.execSQL(sqlInsertEnc + " (30100,503,'\"Gate-portal?\" buzzes its mouths within your head. \"All time-places are gate-portals.\" You grasp at its meaning, the favor of fortune your only defense against an accidental and disastrous misunderstanding. Pass a <b>Luck (-2) check</b> or take this card. While you have it, your difficulty to close gates is increased by 1. Discard this card when you close a gate.')");
		db.execSQL(sqlInsertCardEnc + " (6034,30101)");
		db.execSQL(sqlInsertEnc + " (30101,512,'In the House of Shuggob, many dark secrets are known, and revealed to you. Draw a Spell.')");
