		db.execSQL(sqlInsertEnc + " (30103,512,'Shuggob will share his secrets, but won''t you sample his fine meats? You may draw a Spell if you wish, but if you do, lose Sanity equal to the drawn Spell''s Sanity cost.')");
		db.execSQL(sqlInsertCardEnc + " (6035,30104)");
		db.execSQL(sqlInsertEnc + " (30104,505,'Even a brief visit sees the scales fall from your eyes; every word of recorded knowledge in all places and times is multiplied in your understanding. Take a Clue token for every <i><b>Tome</b></i> you possess.')");
