		db.execSQL(sqlInsertEnc + " (30105,500,'Strength- and knowledge?- in numbers. Take a Clue token for every investigator, including you, currently in an Other World.')");
		db.execSQL(sqlInsertCardEnc + " (6036,30106)");
		db.execSQL(sqlInsertEnc + " (30106,507,'The High Priest at the great stone temple demands a sacrifice. Discard your most expensive Unique Item. If several equally expensive items qualify, choose one to discard.')");
		db.execSQL(sqlInsertCardEnc + " (6036,30107)");
