		db.execSQL(sqlInsertCardEnc + " (6039,30115)");
		db.execSQL(sqlInsertEnc + " (30115,512,'How can these winds of change blow so fiercely beneath the earth, where weather itself will not descend? Pass a <b>Will (-2) check</b> or move all of your skill sliders to their leftmost positions.')");
