		db.execSQL(sqlInsertEnc + " (30117,500,'\"It is not too late,\" whispers the voice in the darkness. If the terror level is less than 6, draw a Spell.')");
		db.execSQL(sqlInsertCardEnc + " (6040,30119)");
		db.execSQL(sqlInsertEnc + " (30119,512,'If only your hasty disguise will hold in this city of ghouls! The things you might learn! Pass a <b>Luck (-2) check</b> to gain 1 Clue token, 1 Stamina, 1 Sanity, and return to Arkham.')");
		db.execSQL(sqlInsertCardEnc + " (6040,30121)");
		db.execSQL(sqlInsertEnc + " (30121,505,'Knowledge empowers the body and spirit for those who brave the stars to visit this place. Gain 1 Sanity and 1 Stamina for each Spell you possess.')");
