		db.execSQL(sqlInsertEnc + " (30120,506,'You find yourself reliving a moment in your past that you have always regretted, only this time everything goes as well as you could ever hope. Gain 2 Sanity.')");
		db.execSQL(sqlInsertCardEnc + " (6041,30122)");
		db.execSQL(sqlInsertEnc + " (30122,500,'At the top of the hill is an old stone well. You may toss $1 inside and pass a <b>Luck (-2) check</b> to be <i>Blessed</i>. If you fail, nothing happens.')");
		db.execSQL(sqlInsertCard + " (6042)");
