		db.execSQL(sqlInsertCardColor + " (6043,4)");
		db.execSQL(sqlInsertCardEnc + " (6043,30126)");
		db.execSQL(sqlInsertEnc + " (30126,500,'A ghostly ship captain offers you passage home, for a price. Return to Arkham, but you are <i>Cursed</i>.')");
		db.execSQL(sqlInsertCardEnc + " (6043,30127)");
		db.execSQL(sqlInsertEnc + " (30127,503,'You feel other minds trying to contact you and warn you of something. Gain 2 Clue tokens.')");
