		db.execSQL(sqlInsertCardColor + " (6044,4)");
		db.execSQL(sqlInsertCardEnc + " (6044,30129)");
		db.execSQL(sqlInsertEnc + " (30129,500,'A wounded beast snarls at you, but it can''t move. You don''t think it''s going to live much longer. If you want to wait around, you are <i>delayed</i>, but you may draw a monster from the cup and take it as a monster trophy, even if it has the <b>Endless</b> ability.')");
		db.execSQL(sqlInsertCardEnc + " (6044,30130)");
		db.execSQL(sqlInsertEnc + " (30130,507,'One of the villagers has a prized possession of unknown origins. Pass a <b>Sneak (+1) check</b> to successfully claim it as he sleeps. Draw 1 Common Item.')");
