		db.execSQL(sqlInsertEnc + " (30133,507,'The villagers offer you a little sustenance. Gain 1 Stamina.')");
		db.execSQL(sqlInsertCardEnc + " (6045,30134)");
		db.execSQL(sqlInsertEnc + " (30134,505,'It is recommended to you to simply open a book at random to find the answers you seek. If you would like to give it a try, make a <b>Luck (-2) check</b>. If you pass, gain 2 Clue tokens. If not, you still gain 2 Clue tokens but you are <i>delayed</i>.')");
		db.execSQL(sqlInsertCard + " (6046)");
