		db.execSQL(sqlInsertCardEnc + " (6046,30135)");
		db.execSQL(sqlInsertEnc + " (30135,500,'In the dark, you stumble into a deep hole. Looking up, you see a gravestone bearing your name. Pass a <b>Speed (-2) check</b> to scramble out or lose 2 Sanity and be <i>delayed</i>.')");
		db.execSQL(sqlInsertCardEnc + " (6046,30136)");
		db.execSQL(sqlInsertEnc + " (30136,507,'A monster appears from the shadows!')");
		db.execSQL(sqlInsertCardEnc + " (6046,30137)");
		db.execSQL(sqlInsertEnc + " (30137,503,'You read a detailed description of humanity''s extinction. Pass a <b>Will (-1) check</b> or lose 2 Sanity.')");
