		db.execSQL(sqlInsertEnc + " (30195,509,'You recognize the building you are in as a library, but it is necessary to operate strange machines to find the books you seek. Make a <b>Lore (-2) check</b>. If you pass, gain 2 Clue tokens and then search the Common Item deck and take the first <i><b>Tome</b></i> you find.')");
		db.execSQL(sqlInsertCardEnc + " (6068,30196)");
		db.execSQL(sqlInsertEnc + " (30196,506,'The Zoogs are making so much noise you don''t hear the other creatures in this forest. A monster appears. Treat this monster as if it had the <b>Ambush</b> special ability.')");
