		db.execSQL(sqlInsertEnc + " (30202,500,'A strange song conjures up images of a life you could have lived. Gain Clue tokens equal to your focus (to a maximum of 4).')");
		db.execSQL(sqlInsertCardEnc + " (6071,30203)");
		db.execSQL(sqlInsertEnc + " (30203,507,'The icy winds tear at your flesh, and the plains seem endless. Pass a <b>Fight (-1) check</b> or lose 1 Stamina, and pass a <b>Will (-1) check</b> or lose 1 Sanity. If Ithaqua is the Ancient One, the modifiers for these rolls are both reduced to -3.')");
		db.execSQL(sqlInsertCardEnc + " (6071,30204)");
		db.execSQL(sqlInsertEnc + " (30204,503,'They''ve been collecting people! Make a <b>Lore (-2) [2] check</b> to determine how to release the victims. If you pass, you may search the Ally deck and take any Ally of your choice. If you fail, you are taken captive yourself, and are <i>delayed</i>.')");
