		db.execSQL(sqlInsertEnc + " (1426,39,'An artist is sketching a woman sitting across from him. You can tell, just by looking as his rendering of her, the two are very much in love. Gain 1 Sanity from this heartwarming scene.')");
		db.execSQL(sqlInsertCardEnc + " (427,1427)");
		db.execSQL(sqlInsertEnc + " (1427,41,'Someone sneaks a book in with your other belongings. Make a <b>Luck (-1) check</b>. If you pass, gain 1 Spell. If you fail, it is a text that describes the agonizing horrors befalling humanity. Lose 1 Sanity.')");
		db.execSQL(sqlInsertCardEnc + " (427,1428)");
