		db.execSQL(sqlInsertCardEnc + " (132,461)");
		db.execSQL(sqlInsertEnc + " (461,22,'Pass a <b>Luck (-1) check</b> to find a hidden cache concealed in the wall of an upstairs bedroom. Draw 1 Unique Item.')");
